package net.conn;

import java.io.File;
import java.io.FileOutputStream;
import java.util.Base64;

public class WoffTest {
    // 猫眼的字体是data url 格式，格式是data:application/font-woff;charset=utf-8;base64,   +base64编码字符串，是把文件用base64编码成字符串写入到网页中，所以只需要用base64解码后边的字符串。Base64包是jdk 1.8新出的
    public void woff(){
        String base64Str="";
        final Base64.Decoder decoder = Base64.getDecoder();
        try{
            byte[] bt=decoder.decode(base64Str);
            File file=new File("D:\\yunzhi\\lin\\text.woff");
            file.createNewFile();
            FileOutputStream in=new FileOutputStream(file);
            in.write(bt,0,bt.length);
            in.close();
        }catch (Exception e){
            e.printStackTrace();
        }
    }
}
