# encoding:utf-8
import base64
from io import BytesIO
from fontTools.ttLib import TTFont
import sys

#base64_str="";
base64_str=sys.argv[1]
#price_code="龤麣鑶鑶元/月    押一付三";
#content="龤室驋厅驋卫,齤齤平  精装修 "
txt_code=sys.argv[2]

def get_page_show_ret(string):
    font = TTFont(BytesIO(base64.decodebytes(base64_str.encode())))
    c = font['cmap'].tables[0].ttFont.tables['cmap'].tables[0].cmap
    ret_list = []
    for char in string:
        decode_num = ord(char)
        if decode_num in c:
            num = c[decode_num]
            num = int(num[-2:])-1
            ret_list.append(num)
        else:
            ret_list.append(char)
    ret_str_show = ''
    for num in ret_list:
        ret_str_show += str(num)
    return ret_str_show
retxt=""
retxt=get_page_show_ret(txt_code);
print(retxt)